This is one of my first programs.  /**
 * @(#)Adminapp.java
 *
 * Admin application
 *Determine if user is a potential canidate for a nnjg admin.
 * Aaron Ivy
 * @version 1.00 2011/11/19
 */

 import java.util.Scanner;

public class Adminapp {

    public static void main(String[] args) {

    	Scanner reader = new Scanner(System.in);

    	int forumposts;
    	int timeonservers;
    	int mic;
    	String username;
    	int hateclub;

    	System.out.println (" Please use 1 for yes and 0 for no, please use underscores for spaces ");

    	System.out.print (" Please enter your username ");
    	username = reader.next();

    	System.out.print (" How many forum posts do you have? ");
    	forumposts = reader.nextInt();

    	System.out.print (" On average how many hours do you spend on nnjg servers weekly? ");
    	timeonservers = reader.nextInt();

    	System.out.print (" Do you possess and use a good microphone? ");
    	mic = reader.nextInt();

    	System.out.print (" Do you belong to a hate group involving NNJG? ");
    	hateclub = reader.nextInt();

    	if ( forumposts >=100 && timeonservers >= 10 && mic == 1 && hateclub == 0)
    		System.out.println (username + " " + "You have a good chance of becoming an admin");
    			else
    				System.out.println ("you Have no chance of becoming an admin");
    }
}

